import $ from 'jquery';
import React from 'react';
import logo from './logo.svg';
import './App.css';

import { Container } from "semantic-ui-react"


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      activeArray:[0,0,0,0],
      active:""
    };
    this.clickHeader = this.clickHeader.bind(this);
    this.serverRequest = this.serverRequest.bind(this);
  }
  clickHeader(){
    var arr = [0,0,0,0]
    arr[id] = 1
    this.setState({activeArray:arr, active:active})
    console.log(id,active)
  }
  serverRequest(){
    $.get("http://localhost:5000/api/logs/items", res => {
      console.log(res);
      this.setState({
        items: res
      },()=>{
        console.log(this.state.items[0].name)
      });
    });
  }
  componentDidMount(){
    this.serverRequest();
    console.log(this.state.items[2])
  }
  render() {
    return (
      <div className="App">
        <Header/>
        <Container style={{marginTop:"5em"}}>
          <h2>Showing your logs here!</h2>
          <ItemTable items={this.state.items}/>
        </Container>
      </div>  
    )
  }
}
class Header extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return(
      <div className="ui inverted top fixed menu">
        <div className="ui container">
          <a id = {0} className="header item" onClick={() => 
            props.handleClick()}>
            Hockey Stick
          </a>
          <a id = {1} className="item">Home</a>
          <a id = {2} className="{this.state.activeArray[2]} item" 
            clickHeader={this.clickHeader}>Logs</a>
          <a id = {3} className="item">Export</a>
        </div>  
      </div>
    )
  }
}

function ItemTable(props) {
  return (
    <div className="ui container">
      <table className="ui selectable celled table">
        <thead>
          <tr>
            <th>Date</th>
            <th>Name</th>
            <th>Category</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {props.items.map(item => (
            <Item item={item}/>
          ))}
        </tbody>
      </table>
    </div>  
  )
}

function Item(props) {
  return (
    <tr key={props.item.id}>
      <td>{props.item.date}</td>
      <td>{props.item.name}</td>
      <td>{props.item.category}</td>
      <td>¥{props.item.price}</td>
    </tr>
  )
}

export default App;
