import $ from 'jquery';
import React from 'react';
import logo from './logo.svg';
import './App.css';

import { Container } from "semantic-ui-react"


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: []
    };
    this.serverRequest = this.serverRequest.bind(this);
  }

  serverRequest(){
    $.get("http://localhost:5000/api/logs/items", res => {
      console.log(res);
      this.setState({
        items: res
      },()=>{
        console.log(this.state.items[0].name)
      });
    });
  }
  componentDidMount(){
    this.serverRequest();
    console.log(this.state.items[2])
  }
  render() {
    return (
      <div className="App">
        <Header/>
        <Container style={{marginTop:"5em"}}>
          <h2>Showing your logs here!</h2>
          <ItemTable items={this.state.items}/>
        </Container>
      </div>  
    )
  }
}
class Header extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return(
      <div className="ui inverted top fixed menu">
        <div className="ui container">
          <a className="header item">
            Hockey Stick
          </a>
          <a className="item">Home</a>
          <a className="active item">Logs</a>
          <a className="item">Export</a>
        </div>  
      </div>
    )
  }
}

function ItemTable(props) {
  return (
    <div className="ui container">
      <table className="ui selectable celled table">
        <thead>
          <tr>
            <th>Date</th>
            <th>Name</th>
            <th>Category</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {props.items.map(item => (
            <Item item={item}/>
          ))}
        </tbody>
      </table>
    </div>  
  )
}

function Item(props) {
  return (
    <tr key={props.item.id}>
      <td>{props.item.date}</td>
      <td>{props.item.name}</td>
      <td>{props.item.category}</td>
      <td>Â¥{props.item.price}</td>
    </tr>
  )
}

export default App;
