import $ from 'jquery';
import React from 'react';
import {
  BarChart, Bar, Brush, ReferenceLine, XAxis, YAxis,
  CartesianGrid, Tooltip, Legend,
} from 'recharts';

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      goal: 150,
    };
    this.serverRequest = this.serverRequest.bind(this);
  }
  serverRequest(){
    $.get("http://localhost:5000/api/logs/items", res => {
      console.log(res);
      this.setState({
        items: res
      },()=>{
        console.log(this.state.items[0].name)
      });
    });
  }
  componentDidMount(){
    this.serverRequest();
  }
  render() {
    return (
      <div>
        <h1>Hello, Soya</h1>
        <Chart datas={this.state.items} goal={this.state.goal}/>
      </div>
    )
  }
}
function Chart(props) {
  return (
      <BarChart
        width={500}
        height={300}
        data={props.datas}
        margin={{
          top: 5, right: 30, left: 20, bottom: 5,
        }}
      >
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="id" />
        <YAxis dataKey="price" />
        <Tooltip />
        <Legend verticalAlign="price" height={30} stroke="#8884d8" />
        <ReferenceLine y={0} stroke="#000" />
        <Brush dataKey="id" height={30} stroke="#8884d8" />
        <Bar dataKey="price" fill="#8884d8" />
      </BarChart>
  )
}
export default Home;
